void main() {
  // –°–æ–∑–¥–∞–Ω–∏–µ –º–Ω–æ–∂–µ—Å—Ç–≤
  var numbers = {1, 2, 3, 2, 1}; // –î—É–±–ª–∏–∫–∞—Ç—ã —É–¥–∞–ª—è—é—Ç—Å—è
  print('–ß–∏—Å–ª–∞: $numbers'); // {1, 2, 3}

  var fruits = {'—è–±–ª–æ–∫–æ', '–±–∞–Ω–∞–Ω'};

  // –î–æ–±–∞–≤–ª–µ–Ω–∏–µ
