  fruits.add('–±–∞–Ω–∞–Ω'); // –ù–µ –¥–æ–±–∞–≤–∏—Ç—Å—è (—É–∂–µ –µ—Å—Ç—å)
  print('–§—Ä—É–∫—Ç—ã: $fruits');

